	    /*** common css ***/
	    * 						{ box-sizing: border-box; }
	    a:hover,
	    a:focus 				{ text-decoration: none; }
	    body 					{ font-family: 'Montserrat', sans-serif; font-weight: 400; }
	    h1 						{ font-size: 72px; line-height: 73px; letter-spacing: 2px; color: #fff;}
	    h2 						{ font-size: 56px; line-height: 68px; letter-spacing: 18px; color: #000; text-transform: uppercase; text-shadow: 12px 9px 6px rgba(0,0,0,0.10); }
	    h3 						{ margin: 0 0 16px; font-size: 48px; line-height: 58px; font-weight: 600;  }
	    h4 						{ margin: 0 0 6px; font-size: 24px; line-height: 26px; font-weight: 600; text-transform: capitalize; }
	    h1,h2 					{ margin: 0px; font-weight: 700; }
	    h4,p,.client-name 		{ font-family: 'Raleway', sans-serif; color: #212121;}
	    ul 						{ margin: 0; padding: 0;  list-style: none;}
	    p 						{ margin: 0; font-size: 18px; line-height: 30px;  font-weight: 500; }
	    .small 					{ margin-bottom: 8px; font-size: 16px; line-height: 27px; font-family: 'Raleway', sans-serif; font-weight: 600; color: #757575; }
	    .client-name 			{ font-weight: 700; text-transform: capitalize;}
	    .common-btn 			{ padding: 18px 20px; border:1px solid #fff; font-size: 16px; line-height: 18px; color: #fff; text-transform: uppercase; transition: all 0.3s ease-in-out; -webkit-transition: all 0.3s ease-in-out; display: inline-block;}
