    /*** common css  ***/
    *                                 { box-sizing: border-box; text-decoration: none;}
    a:hover                           { text-decoration: none; }
    *:focus                           { outline: none; box-shadow: none; }
    body                              { font-family: 'Raleway', sans-serif; font-weight: 400; }
    h1                                { font-size: 56px; line-height: 76px; color: #fff; text-transform: capitalize;}
    h2                                { font-size: 32px; line-height: 46px; }
    h3                                { font-size: 18px; line-height: 22px; font-family: 'Raleway', sans-serif; font-weight: 500; text-transform: capitalize; }
    h1,h2                             { font-family: 'Manuale', serif; font-weight: 700; }
    h1,h2,h3,h4,h5,h6                 { margin: 0; }
    p                                 { margin: 0 0 8px; font-size: 14px; line-height: 22px;}
    p:last-child                      { margin-bottom: 0; }
    a                                 { text-decoration: none; }
    .blue-title                       { color: #395896;}
    .gray                             { color: #737373; }
    p.sub-text                        { color: #A7B7D6; font-weight: 600; text-transform: uppercase; position: relative; display: inline-block;}
    p.sub-text.line::after            { content: ""; height: 2px; width: 300px; background: #A7B7D6; display: inline-block; position: absolute; left: calc(100% + 1px); top: 50%;}
    p,
    .nav li a,
    .contact-section a span           { font-weight: 500; color: #fff; }
    ul                                { margin:0; padding: 0; list-style: none; }
    .custom-container                 { padding: 0 15px; margin: 0 auto; }
    .clear                            { clear: both; } 
    .common-padding                   { padding:80px 0; }
    .common-btn                       { padding: 12px 16px; display: inline-block; font-size: 14px; line-height: 16px; color: #fff; text-transform: uppercase; font-weight: 700; border:2px solid #ccc; transition: all 0.3s ease-in-out; -webkit-transition: all 0.3s ease-in-out; -o-transition: all 0.3s ease-in-out;}
    .common-btn:hover                 { background-color: #fff; color: #395896; border-color: #fff;  }
    .common-btn.blue                  { border-color: #395896 ; color: #395896 ; }
    .common-btn.blue:hover            { background:#395896; color: #fff;  }
    .d-flex                           { display: flex; display: -webkit-flex; align-items: center; }
    .text-center                      { text-align: center; }
    .text-right                       { text-align: right; }
    .content                          { padding-top: 70px; }
